import{_ as d,c as a,o as r,ae as o}from"./chunks/framework.Ai_99reE.js";const g=JSON.parse('{"title":"GitHub vs Backlog Git 比較：VSCode開発者向けガイド","description":"","frontmatter":{},"headers":[],"relativePath":"appendix/github_vs_backlog_git.md","filePath":"appendix/github_vs_backlog_git.md"}'),e={name:"appendix/github_vs_backlog_git.md"};function i(l,t,h,s,n,c){return r(),a("div",null,[...t[0]||(t[0]=[o('<h1 id="github-vs-backlog-git-比較-vscode開発者向けガイド" tabindex="-1">GitHub vs Backlog Git 比較：VSCode開発者向けガイド <a class="header-anchor" href="#github-vs-backlog-git-比較-vscode開発者向けガイド" aria-label="Permalink to &quot;GitHub vs Backlog Git 比較：VSCode開発者向けガイド&quot;">​</a></h1><p>VSCode を使った開発で、GitHub と Backlog Git を使う場合の違いを比較します。どちらも Git ベースの開発には使えますが、課題管理やチーム連携、CI/CD との相性が異なります。</p><hr><h2 id="🌐-構成イメージ-表形式" tabindex="-1">🌐 構成イメージ（表形式） <a class="header-anchor" href="#🌐-構成イメージ-表形式" aria-label="Permalink to &quot;🌐 構成イメージ（表形式）&quot;">​</a></h2><h3 id="●-github-連携の流れ" tabindex="-1">● GitHub 連携の流れ <a class="header-anchor" href="#●-github-連携の流れ" aria-label="Permalink to &quot;● GitHub 連携の流れ&quot;">​</a></h3><table tabindex="0"><thead><tr><th>ステップ</th><th>コンポーネント</th><th>説明</th></tr></thead><tbody><tr><td>1</td><td>VSCode</td><td>開発環境。Gitでソース管理を実施</td></tr><tr><td>2</td><td>GitHub（Gitサーバ）</td><td><code>git push</code> でコードをGitHubに送信</td></tr><tr><td>3</td><td>GitHub Issues / Projects</td><td>課題・タスク・進捗管理をGitHub内で行う</td></tr></tbody></table><hr><h3 id="●-backlog-git-連携の流れ" tabindex="-1">● Backlog Git 連携の流れ <a class="header-anchor" href="#●-backlog-git-連携の流れ" aria-label="Permalink to &quot;● Backlog Git 連携の流れ&quot;">​</a></h3><table tabindex="0"><thead><tr><th>ステップ</th><th>コンポーネント</th><th>説明</th></tr></thead><tbody><tr><td>1</td><td>VSCode</td><td>開発環境。<code>git clone</code> でBacklog Gitと連携</td></tr><tr><td>2</td><td>Backlog Git（Gitサーバ）</td><td>コミットがBacklog Gitサーバに蓄積される</td></tr><tr><td>3</td><td>Backlog課題管理</td><td>コミットメッセージに課題IDを書くと自動で紐付けられる</td></tr><tr><td>4</td><td>ガントチャート等</td><td>課題がプロジェクト計画と連携して進捗が見える化</td></tr></tbody></table><hr><h2 id="📋-基本比較表" tabindex="-1">📋 基本比較表 <a class="header-anchor" href="#📋-基本比較表" aria-label="Permalink to &quot;📋 基本比較表&quot;">​</a></h2><table tabindex="0"><thead><tr><th>項目</th><th>GitHub</th><th>Backlog Git</th></tr></thead><tbody><tr><td><strong>Git管理</strong></td><td>✅ git init → GitHub連携</td><td>✅ git cloneでBacklog Git連携</td></tr><tr><td><strong>課題トラッキング</strong></td><td>Issues</td><td>Backlog課題（チケット）</td></tr><tr><td><strong>コミットと課題の紐付け</strong></td><td><code>#123 fix</code> などで自動連携</td><td><code>PROJ-123 #comment</code> 明示的な紐付け</td></tr><tr><td><strong>コードレビュー</strong></td><td>Pull Request強力なUI/UX</td><td>PR機能ありだがUXはやや劣る</td></tr><tr><td><strong>CI/CD連携</strong></td><td>GitHub Actionsで自動デプロイ等</td><td>連携は手動（Jenkins等と組み合わせ）</td></tr><tr><td><strong>ドキュメント機能</strong></td><td>README / Wiki</td><td>Documents（Mermaid対応）</td></tr><tr><td><strong>プロジェクト管理</strong></td><td>Projects（軽量）</td><td>ガントチャート・バーンダウン対応</td></tr><tr><td><strong>料金</strong></td><td>無料プランあり</td><td>有料プラン（課題数・人数制限あり）</td></tr><tr><td><strong>OSS・外部連携</strong></td><td>◎ 公開・共有が得意</td><td>△ 基本社内向け</td></tr></tbody></table><hr><h2 id="✅-backlog-gitを使うメリット" tabindex="-1">✅ Backlog Gitを使うメリット <a class="header-anchor" href="#✅-backlog-gitを使うメリット" aria-label="Permalink to &quot;✅ Backlog Gitを使うメリット&quot;">​</a></h2><ul><li>チケット（課題）と Git コミットが一元管理可能</li><li>PM・QA など非開発者と共通のUIで管理しやすい</li><li>開発だけでなく「案件進行」全体の見える化に強い</li></ul><h2 id="❌-デメリット" tabindex="-1">❌ デメリット <a class="header-anchor" href="#❌-デメリット" aria-label="Permalink to &quot;❌ デメリット&quot;">​</a></h2><ul><li>CI/CD 機能は外部連携頼み</li><li>OSSプロジェクトには不向き</li><li>PRまわりのレビュー体験はGitHubより劣る</li></ul><hr><h2 id="🧭-利用シーン別のおすすめ" tabindex="-1">🧭 利用シーン別のおすすめ <a class="header-anchor" href="#🧭-利用シーン別のおすすめ" aria-label="Permalink to &quot;🧭 利用シーン別のおすすめ&quot;">​</a></h2><table tabindex="0"><thead><tr><th>利用シーン</th><th>おすすめ</th></tr></thead><tbody><tr><td>社内案件、チームでの進捗管理重視</td><td>✅ Backlog Git</td></tr><tr><td>OSS、CI/CD重視、公開性必要</td><td>✅ GitHub</td></tr><tr><td>少人数・軽量開発</td><td>✅ GitHub</td></tr><tr><td>課題・コード・進捗の一元管理</td><td>✅ Backlog</td></tr></tbody></table><hr><h2 id="🎯-まとめ" tabindex="-1">🎯 まとめ <a class="header-anchor" href="#🎯-まとめ" aria-label="Permalink to &quot;🎯 まとめ&quot;">​</a></h2><p>どちらも VSCode での開発は可能ですが、<strong>「課題中心の開発」を重視するならBacklog</strong>、<strong>「コード中心のCI/CDと公開開発」を重視するならGitHub</strong> が向いています。</p><p>目的に応じて選びましょう。</p>',24)])])}const u=d(e,[["render",i]]);export{g as __pageData,u as default};
